body.script:
$('#username').focus()
$('#form').submit(function (e) {
    e.preventDefault();
    var h = Sha1.hash($('#password').val());
    var r = Sha1.hash($('#challenge').val() + h);
    $.ajax({
        url: '/auth',
        type: 'POST',
        data: {
            username: $('#username').val(),
            response: r
        },
        success: function () {
            $('#form').unbind('submit');
            $('#password').val('');
            $('#login').click();
        }
    });
});
